import java.util.Scanner;

public class PalindromeSentence {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        System.out.print("문장을 입력하세요: ");
        String sentence = scanner.nextLine();

        if (isPalindrome(sentence)) {
            System.out.println("입력한 문장은 회문입니다.");
        } else {
            System.out.println("입력한 문장은 회문이 아닙니다.");
        }
    }

    public static boolean isPalindrome(String sentence) {
        String cleanSentence = removeNonAlphanumeric(sentence);

        int length = cleanSentence.length();
        for (int i = 0; i < length / 2; i++) {
            if (cleanSentence.charAt(i) != cleanSentence.charAt(length - 1 - i)) {
                return false;
            }
        }

        return true;
    }

    public static String removeNonAlphanumeric(String sentence) {
        return sentence.replaceAll("[^a-zA-Z0-9]", "");
    }
}

