#include <iostream>

class Node {
public:
    int data;
    Node* next;

    Node(int data) {
        this->data = data;
        next = nullptr;
    }
};

class LinkedList {
private:
    Node* head;

public:
    LinkedList(int arr[], int length) {
        if (length <= 0) {
            head = nullptr;
            return;
        }

        head = new Node(arr[0]);
        Node* current = head;
        for (int i = 1; i < length; i++) {
            Node* newNode = new Node(arr[i]);
            current->next = newNode;
            current = current->next;
        }
    }

    ~LinkedList() {
        Node* current = head;
        while (current != nullptr) {
            Node* temp = current;
            current = current->next;
            delete temp;
        }
    }

    void print() {
        Node* current = head;
        while (current != nullptr) {
            std::cout << current->data << " ";
            current = current->next;
        }
    }
};

int main() {
    int arr[] = {1, 2, 3, 4, 5};
    int length = sizeof(arr) / sizeof(arr[0]);

    LinkedList list(arr, length);
    list.print();

    return 0;
}

def longest_decreasing_subarray(arr):
    if len(arr) == 0:
        return 0

    dp = [1] * len(arr)
    max_length = 1

    for i in range(1, len(arr)):
        if arr[i] < arr[i-1]:
            dp[i] = dp[i-1] + 1
            max_length = max(max_length, dp[i])

    return max_length

